while True: print(''.join(map(lambda x: chr(ord(x[0])+(ord(x[1])%26)), (y+((input('Enter a character key: ') or '4')[0]) for y in list(input('What is your text?\n') or ' '))))) if ((input('Encrypt or Decrypt: ') or 'e').lower())[0] == 'e' else print(''.join(map(lambda x: chr(ord(x[0])-(ord(x[1])%26)), (y+((input('Enter a character key: ') or '4')[0]) for y in list(input('What is your text?\n') or ' ')))))
